mapper.update:
update dish
set name = #{name}, price = #{price} , category_id = #{categoryId}, image = #{image}, description = #{description}
where id = #{id}

update dish
set status = #{status}
where id in (

        #{id}

    )

mapper.delete:
delete from dish
where id in (

        #{id}

    )

mapper.select:
select d.id, d.name, d.category_id, d.price, d.code, d.image, d.description, d.status, d.sort, d.create_time, d.update_time, d.create_user, d.update_user ,c.name categoryName from dish d right join category c on d.category_id = c.id where is_deleted = 0
and d.name like #{name}
order by sort desc, update_time desc limit #{page},#{pageSize}
